mapper.select:
WITH SORTEDBOARD AS ( SELECT BO_NO, BO_TITLE, BO_WRITER, BO_DATE, BO_HIT, BO_REC FROM FREEBOARD
ORDER BY BO_NO DESC ), RNUMBOARD AS ( SELECT ROWNUM RNUM, SORTEDBOARD.* FROM SORTEDBOARD ) SELECT * FROM RNUMBOARD
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

	SELECT FREEBOARD_SEQ.nextval
	FROM DUAL

INSERT INTO FREEBOARD
(
	BO_NO
	, BO_TITLE
	, BO_WRITER
	, BO_IP
	, BO_MAIL
	, BO_PASS
	, BO_CONTENT
	, BO_DATE
)VALUES
(
	#{boNo,jdbcType=NUMERIC}
	, #{boTitle,jdbcType=VARCHAR}
	, #{boWriter,jdbcType=VARCHAR}
	, #{boIp,jdbcType=VARCHAR}
	, #{boMail,jdbcType=VARCHAR}
	, #{boPass,jdbcType=VARCHAR}
	, #{boContent,jdbcType=CLOB}
	, SYSDATE
)

mapper.update:
UPDATE FREEBOARD
SET
	BO_TITLE = #{boTitle,jdbcType=VARCHAR}
	, BO_MAIL = #{boMail,jdbcType=VARCHAR}
	, BO_CONTENT = #{boContent,jdbcType=CLOB}
WHERE BO_NO = #{boNo}
